const bytecode= ""

class App extends Component {
  constructor() {
    super();
    this.state={
      account:null,
      incorrect:false,
      web3:undefined,
      deploying:false,
      deployed:false,
      hash:""
    }
  }
  componentDidMount= async () => {
    //from 2NOv 2018 onwards this convention will be followed
    const web3 = new Web3(window.ethereum)
    try {
      // Request account access if needed
      await window.ethereum.enable()
      // Acccounts now exposed
      const accounts = await web3.eth.getAccounts()

      if (accounts.length) {
        console.log(accounts);
        this.setState({account:accounts[0],web3:web3});
      } else {
        alert("You've locked your MetaMask")
      }

      web3.currentProvider.publicConfigStore.on('update', res => {
        this.setState({account:res.selectedAddress})
        console.log(this.state.account);
      })
    } catch (error) {
    }
  }
 /* Handle form submission */
 generateToken= async(symbol,name,supply,decimals) => {
  var myContract=new this.state.web3.eth.Contract(abi);
  myContract.deploy({
    data: bytecode,
    arguments: [supply,name,symbol,decimals]
})
.send({
    from: this.state.account,
}, function(error, transactionHash){
  this.setState({deploying:true,hash:transactionHash});
 }.bind(this))
 .on("error",(error)=>{
   alert(error)
 })
 .on("receipt",(receipt)=>{
  this.setState({deploying:false,deployed:true,hash:receipt.contractAddress});
 })

 }
 //render//
  render() {
    let metam; 
    if (this.state.account==null||this.state.account===undefined) {
          metam = <div className="metamask">
          <i className="fa fa-circle" style={{"color":"red","margin":"4px"}}></i>
                  Metamask Locked
       </div>;
    } else {
          metam = <div className="metamask">
          <i className="fa fa-circle" style={{"color":"green","margin":"4px"}}></i>
                  Metamask Unlocked
       </div>;
    }
    return (
      <div className="App">
        <Header/>
         {metam}
        <Form genrateToken={this.generateToken.bind(this)}/>
        {this.state.deploying?<h3>Your Transaction hash is this.state.hash Kindly wait for your transaction to complete</h3>:""}
        {this.state.deployed?<h3>Your contract address is this.state.hash</h3>:""}
      </div>
    );
  }
}

export default App;
