import React, { Component } from 'react';
import Header from "./components/Header.js";
import './App.css';
import Web3 from 'web3';
import Form from "./components/Form.js"
class App extends Component {
  constructor() {
    super();
    this.state={
      account:null
    }
  }
  componentDidMount= async () => {
    //from 2NOv 2018 onwards this convention will be followed
    const web3 = new Web3(window.ethereum)
    try {
      // Request account access if needed
      await window.ethereum.enable()
      // Acccounts now exposed
      const accounts = await web3.eth.getAccounts()

      if (accounts.length) {
        console.log(accounts);
        this.setState({account:accounts[0]});
      } else {
        alert("You've locked your MetaMask")
      }

      // web3.currentProvider.publicConfigStore.on('update', res => {
      //   Web3Store.set(() => ({
      //     isAvailable: true,
      //     account: res.selectedAddress,
      //     network: res.networkVersion,
      //   }))
      // })
    } catch (error) {
      console.log(error)
      // User denied account access...
    }
  }
  getWeb3State() {
    if(this.state.account==null||this.state.account===undefined) {
      return ( <div className="metamask">
      <i className="fa fa-circle" style={{"color":"red","margin":"4px"}}></i>
              Metamask Locked
   </div>)
    }
    else return( <div className="metamask">
    <i className="fa fa-circle" style={{"color":"green","margin":"4px"}}></i>
            Metamask Unlocked
 </div>)
  }
  render() {
    let metam; 
    if (this.state.account==null||this.state.account===undefined) {
          metam = <div className="metamask">
          <i className="fa fa-circle" style={{"color":"red","margin":"4px"}}></i>
                  Metamask Locked
       </div>;
    } else {
          button = <div className="metamask">
          <i className="fa fa-circle" style={{"color":"green","margin":"4px"}}></i>
                  Metamask Unlocked
       </div>;
    }
    return (
      <div className="App">
        <Header/>
         {metam}
        <Form/>
      </div>
    );
  }
}

export default App;
