import React, { Component } from 'react';
import Header from "./components/Header.js";
import './App.css';
import Web3 from 'web3';
import Form from "./components/Form.js"

class App extends Component {
  constructor() {
    super();
    this.state={
      account:null,
      incorrect:false,
      web3:undefined
    }
  }
  componentDidMount= async () => {
    //from 2NOv 2018 onwards this convention will be followed
    const web3 = new Web3(window.ethereum)
    try {
      // Request account access if needed
      await window.ethereum.enable()
      // Acccounts now exposed
      const accounts = await web3.eth.getAccounts()

      if (accounts.length) {
        console.log(accounts);
        this.setState({account:accounts[0],web3:web3});
      } else {
        alert("You've locked your MetaMask")
      }

      web3.currentProvider.publicConfigStore.on('update', res => {
        this.setState({account:res.selectedAddress})
        console.log(this.state.account);
      })
    } catch (error) {
    }
  }
 /* Handle form submission */
 generateToken(symbol,name,supply,decimals) {
     console.log(this.state.web3.eth.accounts[0]);
     var standardtokenContract = this.state.web3.eth.contract(process.env.ABI);
     var standardToken=standardtokenContract.new(supply,
      name,
      symbol,
      decimals,
      {
        from:this.state.web3.eth.accounts[0]
      },function(err,result){
        if(err) console.log(err);
        console.log(result);
      }
      )
 }
 //render//
  render() {
    let metam; 
    if (this.state.account==null||this.state.account===undefined) {
          metam = <div className="metamask">
          <i className="fa fa-circle" style={{"color":"red","margin":"4px"}}></i>
                  Metamask Locked
       </div>;
    } else {
          metam = <div className="metamask">
          <i className="fa fa-circle" style={{"color":"green","margin":"4px"}}></i>
                  Metamask Unlocked
       </div>;
    }
    return (
      <div className="App">
        <Header/>
         {metam}
        <Form genrateToken={this.generateToken.bind(this)}/>
      </div>
    );
  }
}

export default App;
